#include<stdio.h>//定义顺序栈结构typedef struct{    int data[StackSize];  //栈中数据    int top;   //指示栈顶位置，为-1时栈为空栈}SeqStack;//初始化void InitStack(SeqStack *S){    S->top = -1;}//栈是否为空int StackEmpty(SeqStack *S){    if(S->top == -1)        return 1;    else        return 0;}//栈是否已满int StackFull(SeqStack *S){    if(S->top == StackSize - 1)        return 1;    else        return 0;}//数据v 压入栈void Push(SeqStack *S, int v){    if(StackFull(S))        printf("Error,the stack is full");    S->data[(++S->top)] = v;}//弹出栈顶数据int Pop(SeqStack *S){    int i;    if(StackEmpty(S) == 1)        printf("Error,the stack is empty");    i = S->data[S->top];    S->top--;    return i;}